    return (
    <div class="form-box">
      <div class="main-form-container">


      <input type="text" class="main-input main-name" name="NAME" placeholder={e.target.value="name" ? "Search by restaurant, cafe name" : "Search by restaurant location"} onfocus="clearText(this)" onblur="replaceText(this)" /> </div>
      <Select options={searchOptions} styles={customStyles} theme={customThemes}  onChange={this.handleChange}className="mb-3" placeholder="Search by" isSearchable/>


            <Button
            className='btn-search'
            buttonSize='btn--large'
            >
            Search
            </Button>
            </div>
        )
}
}


export default Searchbox;
