import React, { useState } from 'react';
import { Button } from '../../button/Button';
/*import { Link } from 'react-router-dom';*/
import './Searchbox.css';
import Select from 'react-select'
import makeAnimated from 'react-select/animated'



const searchOptions = [
    {value: 'name', label:'Name'},
    {value: 'location', label:'Location'}
];




const customStyles = {
  control: base => ({
    ...base,
    height: 50,
    minHeight: 50,
    borderRadius: 0

  })
};

function customThemes (theme){
    return {
      ...theme,
      colors: {
      ...theme.colors,
      primary25: 'red',
      primary: 'red',

}
    }
}


function Searchbox () {
const [result,setResult]=useState(searchOptions.value)
const setResultHandler = e => {
  setResult(e.value);
}
    return (
    <div class="form-box">
      <div class="main-form-container">


      <input type="text" class="main-input main-name"  placeholder={result==="name" ? "Select Name" : (result ==="location" ? "Select Location" : "Select by name ")}/> </div>
      <Select defaultValue={searchOptions[0]}options={searchOptions} styles={customStyles} theme={customThemes}  onChange={setResultHandler}className="mb-3" placeholder="Search by" isSearchable/>
            <Button
            className='btn-search'
            buttonSize='btn--large'
            >
            Search
            </Button>

            </div>
        )
}



export default Searchbox;
