import React, { useState } from 'react';
import { Button } from '../../button/Button';
import './Searchbox.css';
import Select from 'react-select'
import makeAnimated from 'react-select/animated'

const searchOptions = [
    {value: 'name', label:'Search by Name'},
    {value: 'location', label:'Search by Location'}
];

const customStyles = {
  control: styles => ({ ...styles, backgroundColor: 'white' }),
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    const color = chroma(data.color);
    return {
      ...styles,
      backgroundColor: isDisabled ? 'red' : 'blue',
      color: '#FFF',
      cursor: isDisabled ? 'not-allowed' : 'default',
    }
  }
}

function customThemes (theme){
    return {
      ...theme,
      colors: {
      ...theme.colors,
      }
    }
}


function Searchbox () {
const [result,setResult]=useState(searchOptions.value)
const setResultHandler = e => {
  setResult(e.value);
}
    return (
    <div class="search-box">
      <div class="main-search-container">
        <i class="fas fa-search"/>
        <input type="text" class="main-input main-name"  placeholder={result==="name" ? "Type restaurant, cafe... name" : (result ==="location" ? "Type address or nearby place of restaurant" : "Type restaurant, cafe... name")}/>
      </div>
      <Select defaultValue={searchOptions[0]} options={searchOptions} styles={customStyles} theme={customThemes}  onChange={setResultHandler}className="mb-3" placeholder="Search by" isSearchable/>
            <Button
            className='btn-search'
            buttonSize='btn--large'
            >
            Search
            </Button>
    </div>
        )
}



export default Searchbox;
