import React, { useState, useEffect } from 'react';
import { Button } from '../button/Button';
import MobileNavbar from './MobileNavbar'
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Modal } from '../forms/Modal'

function Navbar() {
  const { click, button, showButton, handleClick, closeMobileMenu }
  = MobileNavbar();

  const [showSignUpModal, setShowSignUpModal] = useState(false);
  const [showLogInModal, setShowLogInModal] = useState(false);


  const openSignUpModal = () => {
    setShowSignUpModal(prev=> !prev);
  }

  const openLogInModal = () =>
    setShowLogInModal (!showLogInModal);

    useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className={click ? 'navbar active' : 'navbar'}>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
           Restaurateur<i class="fas fa-utensils" />
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/restaurants'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Restaurants
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/services'
                className='nav-links'
                onClick={closeMobileMenu}
              >
               Services
              </Link>
              </li>
            <li className={click ? "nav-item" : ""}>
               <Button buttonStyle='btn--outline' className="nav-links" onClick={openLogInModal}
                id="login" >LOG IN</Button></li>
              <li className="nav-item">
               <Button buttonStyle='btn--outline' className="nav-links" onClick={openLogInModal}
                id="login" >SIGN UP</Button>
</li>

          </ul>


          <Modal showLogInModal={showLogInModal}
                 setShowLogInModal={setShowLogInModal}/>

          <Modal showSignUpModal={showSignUpModal} setShowSignUpModal={setShowSignUpModal}/>


        </div>
      </nav>
    </>
  );
}

export default Navbar;
