import React, { useState } from "react";
import { useSpring, animated } from "react-spring";
import SignUpForm from '../forms/signup/SignUpForm'
import LoginForm from '../forms/LogInForm'
import './style.css'
import FormSuccess from "../forms/signup/FormSuccess";

export const Btns = ({registration,login}) => {

  const [registrationFormStatus, setRegistartionFormStatus] = useState(registration);
  const [loginFormStatus, setLoginFormStatus] = useState(login);
  const [isSubmitted, setIsSubmitted] = useState(false);


  const loginProps = useSpring({
    left: registrationFormStatus ? -500 : 0, // Login form sliding positions
  });
  const registerProps = useSpring({
    left: registrationFormStatus ? 0 : 500, // Register form sliding positions
  });

  const loginBtnProps = useSpring({
    borderBottom: registrationFormStatus
      ? "solid 0px transparent"
      : "solid 2px #ff0000",  //Animate bottom border of login button
  });
  const registerBtnProps = useSpring({
    borderBottom: registrationFormStatus
      ? "solid 2px #ff0000"
      : "solid 0px transparent", //Animate bottom border of register button
  });

  function registerClicked() {
    setRegistartionFormStatus(true);
    setLoginFormStatus(false)
  }
  function loginClicked() {
    setRegistartionFormStatus(false);
    setLoginFormStatus(true)
  }

  function submitForm() {
    setIsSubmitted(true);
  }


return (
  <div className="form-content">
  <div className="login-register-wrapper">
  <div className="form-buttons">
    <animated.button
      onClick={loginClicked}
      id="loginBtn"
      style={loginBtnProps}
    >
      Log in
    </animated.button>
    <animated.button
      onClick={registerClicked}
      id="registerBtn"
      style={registerBtnProps}
    >
      Sign up
    </animated.button>
  </div>
  <div className="form-group">
    <animated.form action="" id="loginform" style={loginProps}>
      <LoginForm />
    </animated.form>
    <animated.form action="" id="registerform" style={registerProps}>
    {!isSubmitted ? (
      <SignUpForm submitForm={submitForm} />
    ) : (
      <FormSuccess />
    )}
    </animated.form>
  </div>
</div></div>
  );
}

export default Btns;
