import React from 'react';
import './Form.css'
import styled from 'styled-components'
import { MdClose } from 'react-icons/md'
import AnimatedForms from './AnimatedForms'


export const Modal = ({ showSignUpModal, setShowSignUpModal, showLogInModal, setShowLogInModal }) => {

  const CloseModalButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 15px;
  right: 20px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
  color: #fff;
`;

  return (
    <>
     {showSignUpModal ?
     <>
      <CloseModalButton
                aria-label='Close modal'
                onClick={() => setShowSignUpModal(prev => !prev)}
              />

        <AnimatedForms registration={true} loginForm={false}/></> :
        <>
       {showLogInModal ? <><CloseModalButton
                aria-label='Close modal'
                onClick={() => setShowLogInModal(prev => !prev)}
              /><AnimatedForms registration={false} loginForm={true}/></> : null }
        </>
       }
    </>
  );
};
