import React, { useState } from 'react';
import { Button } from '../../button/Button';
/*import { Link } from 'react-router-dom';*/
import './Searchbox.css';
import Select from 'react-select'
import makeAnimated from 'react-select/animated'

const searchOptions = [
    {value: 'name', label:'Search by: Name'},
    {value: 'location', label:'Search by: Location'}
];

function Searchbox () {
    return (
        <div class="form-box">
            <div class="main-form-container">
    <form id="" class="" method="post">
      <input type="text" class="main-input main-name" name="NAME" value="Search by name" onfocus="clearText(this)" onblur="replaceText(this)" /><Select options={searchOptions} className="mb-3" placeholder="Search by"/><input type="text" class="main-input main-location" name="LOCATION" value="Search by city or postal code" onfocus="clearText(this)"
        onblur="replaceText(this)" /><button type="button" class="main-btn"><p class="search-small">SEARCH BY</p><p class="search-large">Name</p></button>
      <ul class="search-description">
        <li>By Location</li>
        <li>By Name</li>
      </ul>

    </form>
  </div>


            <Button
            className='btns search'
            buttonSize='btn--large'
            >
            Search
            </Button>
            </div>
        )
}



export default Searchbox;
