import React, { useState } from 'react';
import { Button } from '../../button/Button';
import './Searchbox.css';
import Select from 'react-select'
import makeAnimated from 'react-select/animated'

const searchOptions = [
    {value: 'name', label:'Search by Name'},
    {value: 'location', label:'Search by Location'}
];

const breakpoints = [500,820];

const mq = breakpoints.map(
  bp => `@media (min-width: ${bp}px)`
);

const customStyles = {
  control: (base, state,styles) => ({
    ...base,
    height: 50,
    minHeight: 50,
    borderRadius: 0,
    color: 'rgb(185, 185, 185)',
      border: state.isFocused ? `1px solid rgb(185, 185, 185)` : `1px solid rgb(185, 185, 185)`,
      // This line disable the blue border
      boxShadow: state.isFocused ? `1px solid rgb(185, 185, 185)` : `1px solid rgb(185, 185, 185)`,
      '&:hover': {
         border: state.isFocused ? `1px solid rgb(185, 185, 185)` : `1px solid rgb(185, 185, 185)`
      },
      cursor: 'pointer',
       [mq[1]]:{
        top: state.hasValue ? '14px' : '31px'
      }
  }),
  placeholder: (defaultStyles) => {
    return {
        ...defaultStyles,
        color: '#ffffff',
    }
}
};

function customThemes (theme){
    return {
      ...theme,
      colors: {
      ...theme.colors,
      primary:` rgb(185, 185, 185)`,
      primary25:` rgb(215, 215, 215)`
      },
    }
}


function Searchbox () {
const [result,setResult]=useState(searchOptions.value)
const setResultHandler = e => {
  setResult(e.value);
}
    return (
    <div class="search-box">
      <div class="main-search-container">
        <i class="fas fa-search"/>
        <input type="text" class="main-input main-name"  placeholder={result==="name" ? "Type restaurant, cafe... name" : (result ==="location" ? "Type address or nearby place of restaurant" : "Type restaurant, cafe... name")}/>
      </div>
      <Select defaultValue={searchOptions[0]} options={searchOptions} styles={customStyles} theme={customThemes}  onChange={setResultHandler}className="select" placeholder="Search by" isSearchable/>
            <Button
            className='btn-search'
            buttonSize='btn--large'
            >
            Search
            </Button>
    </div>
        )
}



export default Searchbox;
