import React, { Component, useState } from 'react';
import { Button } from '../../button/Button';
/*import { Link } from 'react-router-dom';*/
import './Searchbox.css';
import Select from 'react-select'
import makeAnimated from 'react-select/animated'



const searchOptions = [
    {value: 'name', label:'Name'},
    {value: 'location', label:'Location'}
];




const customStyles = {
  control: base => ({
    ...base,
    height: 50,
    minHeight: 50,
    borderRadius: 0

  })
};

function customThemes (theme){
    return {
      ...theme,
      colors: {
      ...theme.colors,
      primary25: 'red',
      primary: 'red',

}
    }
}

class Searchbox extends Component () {
    constructor() {
      super();
      this.state = {
        value: '',
        label:''
      };
      this.listSelectRef = React.createRef();
    }

render () {
    return (
    <div class="form-box">
      <div class="main-form-container">


      <input type="text" class="main-input main-name" name="NAME"  onfocus="clearText(this)" onblur="replaceText(this)" /> </div>
      <Select options={searchOptions} ref={this.listSelectRef} styles={customStyles} theme={customThemes}  onClick={this.getVal} className="mb-3" placeholder="Search by" isSearchable/>


            <Button
            className='btn-search'
            buttonSize='btn--large'
            >
            Search
            </Button>
            </div>
        )
} }



export default Searchbox;
